import { IResponsiveProps } from '@react-pakistan/util-functions';
export interface ISelectProps extends IResponsiveProps {
    /**
     * Specifies that the drop-down list should automatically get focus when the page loads
     * @default false
     */
    autoFocus?: boolean;
    /**
     * Specifies that a drop-down list should be disabled
     * @default false
     */
    disabled?: boolean;
    /**
     * Defines one or more forms the select field belongs to
     * @default ''
     */
    form?: string;
    /**
     * Specifies if there is an error
     * @default false
     */
    hasError?: boolean;
    /**
     * Specifies that multiple options can be selected at once
     * @default false
     */
    multiple?: boolean;
    /**
     * Defines a name for the drop-down list
     * @default ''
     */
    name?: string;
    /**
     * Specifies that the user is required to select a value before submitting the form
     */
    required: boolean;
    /**
     * Defines the number of visible options in a drop-down list
     * @default 0
     */
    size?: number;
}
export interface IOptionProps {
    /**
     * Specifies that an option should be disabled
     * @default false
     */
    disabled?: boolean;
    /**
     * Specifies the id of the option
     */
    id: string;
    /**
     * Specifies a shorter label for an option
     */
    label: string;
    /**
     * Specifies that an option should be pre-selected when the page loads
     * @default false
     */
    selected?: boolean;
    /**
     * Specifies the value to be sent to a server
     */
    value: string;
}
